mapper.select:
select candidate_id, candidate_name, mobile, person_category, unit_id, person_state, del_flag, create_time, update_time, create_by, update_by, remark
from candidate_info where del_flag = 0

    AND candidate_id = #{candidateId}


    AND candidate_name = #{candidateName}


    AND unit_id = #{unitId}


    AND mobile = #{mobile}

SELECT ROW_NUMBER() OVER (ORDER BY tbl1.create_time) AS row_num, tbl1.candidate_id, tbl1.candidate_name, tbl1.mobile, CASE WHEN tbl1.person_category = '1' THEN '执法司法人员' ELSE '非执法司法人员' END person_category, CASE WHEN tbl1.person_state = '1' THEN '通过' WHEN tbl1.person_state = '0' THEN '不通过' ELSE '待审核' END person_state, tbl1.unit_id, tbl2.dept_name as unit_name, tbl1.del_flag, tbl1.create_time, tbl1.update_time, tbl1.create_by, tbl1.update_by, tbl1.remark FROM candidate_info tbl1 left join sys_dept tbl2 on tbl1.unit_id = tbl2.dept_id where tbl1.del_flag = 0
AND tbl1.candidate_name like concat('%', #{candidateName}, '%')
AND tbl1.mobile like concat('%', #{mobile}, '%')
AND tbl1.unit_id = #{unitId}
AND tbl1.person_state = #{personState}
${params.dataScope}

mapper.update:
update candidate_info
person_state = #{personState}, remark = #{remark}, candidate_name = #{candidateName}, mobile = #{mobile}, person_category = #{personCategory}, unit_id = #{unitId}, update_time = sysdate()
where candidate_id = #{candidateId}

mapper.select:
select distinct tbl1.candidate_id, tbl2.candidate_name, tbl3.dept_name, tbl4.exam_year, tbl4.exam_title, tbl6.dict_label, tbl4.start_time, tbl4.end_time, tbl5.this_score, tbl5.correct_num, tbl5.error_num, CASE WHEN tbl5.correct_num is not null and tbl5.error_num is not null THEN (44-tbl5.correct_num-tbl5.error_num) ELSE 0 END wd_num, tbl5.class_hour from candidate_paper_state tbl1 left join candidate_info tbl2 on tbl1.candidate_id = tbl2.candidate_id left join sys_dept tbl3 on tbl2.unit_id = tbl3.dept_id left join exam_manage tbl4 on tbl1.exam_id = tbl4.exam_id left join (select distinct candidate_id, this_score, correct_num, error_num, exam_id, topic_sort, CASE WHEN class_hour is null THEN 0 ELSE class_hour END class_hour from person_class_hour where del_flag = 0 GROUP BY candidate_id, exam_id, topic_sort) tbl5 on tbl2.candidate_id = tbl5.candidate_id and tbl4.exam_id = tbl5.exam_id and tbl1.topic_sort = tbl5.topic_sort left join sys_dict_data tbl6 on tbl5.topic_sort = tbl6.dict_code where tbl1.del_flag = 0 and tbl1.paper_state = 0 and tbl2.del_flag = 0 and tbl2.candidate_name is not null and tbl3.del_flag = 0 and tbl4.del_flag = 0 and tbl5.class_hour is not null and tbl6.status = 0 and tbl2.candidate_name like concat('%', #{candidateName}, '%') and tbl3.dept_name like concat('%', #{unitName}, '%') and tbl4.exam_year = #{examYear} and tbl4.exam_title like concat('%', #{examTitle}, '%')
AND tbl3.dept_id in
#{unitId}
and tbl6.dict_code = #{topicSort}
${params.dataScope}
