mapper.select:
select tbl4.exam_id, tbl4.exam_title, tbl4.unit_id, tbl4.unit_name, tbl4.exam_year, tbl4.candidate_id, case when sum(tbl5.class_hour) is null then '0' else sum(tbl5.class_hour) end class_hour from ( select tbl1.exam_id, tbl1.exam_title, tbl3.unit_id, tbl3.unit_name, tbl1.exam_year, tbl2.candidate_id from exam_manage tbl1 LEFT JOIN candidate_sign_up tbl2 on tbl1.exam_id = tbl2.exam_id LEFT JOIN unit_manage tbl3 ON tbl2.unit_id = tbl3.unit_id where tbl1.del_flag = '1'
AND tbl1.exam_year = #{examYear}
AND tbl3.unit_name like concat('%', #{unitName}, '%')
AND tbl3.unit_id in
#{item.unitId}
and tbl2.candidate_id is not null ) tbl4 LEFT JOIN person_class_hour tbl5 on tbl4.candidate_id = tbl5.candidate_id and tbl4.exam_id = tbl5.exam_id GROUP BY tbl4.exam_id, tbl4.exam_title, tbl4.unit_id, tbl4.unit_name, tbl4.exam_year, tbl4.candidate_id
${params.dataScope}

select tbl1.candidate_id, tbl1.candidate_name, tbl4.unit_name, tbl2.mobile, tbl3.exam_id, tbl3.exam_year, tbl3.exam_title
from candidate_sign_up tbl1
LEFT JOIN candidate_info tbl2 ON tbl1.candidate_id = tbl2.candidate_id
LEFT JOIN exam_manage tbl3 ON tbl1.exam_id = tbl3.exam_id
LEFT JOIN unit_manage tbl4 ON tbl1.unit_id = tbl4.unit_id
where tbl1.del_flag = 1
and tbl2.del_flag = 1
and tbl3.del_flag = 1
and tbl4.del_flag = 1

    AND tbl4.unit_id = #{unitId}


    AND tbl1.candidate_name = #{candidateName}


    AND tbl2.mobile = #{mobile}


    AND tbl3.exam_id = #{examId}


    AND tbl3.exam_year = #{examYear}


${params.dataScope}
