mapper.select:
select tbl2.candidate_id, tbl1.dept_id as unit_id, tbl1.dept_name as unit_name, tbl4.exam_year, case when sum(tbl5.class_hour) is null then '0' else sum(tbl5.class_hour) end class_hour from sys_dept tbl1 LEFT JOIN candidate_info tbl2 ON tbl1.dept_id = tbl2.unit_id LEFT JOIN candidate_sign_up tbl3 ON tbl2.candidate_id = tbl3.candidate_id and tbl2.unit_id = tbl3.unit_id LEFT JOIN exam_manage tbl4 ON tbl3.exam_id = tbl4.exam_id LEFT JOIN person_class_hour tbl5 ON tbl2.candidate_id = tbl5.candidate_id and tbl3.exam_id = tbl5.exam_id where tbl1.del_flag = 0 and tbl2.del_flag = 0 and tbl3.del_flag = 0 and tbl4.del_flag = 0 and tbl5.del_flag = 0 and tbl4.publish_state = 1
AND tbl4.exam_year = #{examYear}
AND tbl1.dept_name like concat('%', #{unitName}, '%')
AND tbl1.dept_id in
#{unitId}
GROUP BY tbl1.dept_id, tbl2.candidate_id, tbl1.dept_name, tbl4.exam_year
${params.dataScope}

select DISTINCT tbl2.candidate_id, tbl2.candidate_name, tbl1.dept_name as unit_name, tbl2.mobile, tbl4.exam_year
from sys_dept tbl1 LEFT JOIN candidate_info tbl2 ON tbl1.dept_id = tbl2.unit_id
LEFT JOIN candidate_sign_up tbl3 ON tbl2.candidate_id = tbl3.candidate_id AND tbl2.unit_id = tbl3.unit_id
LEFT JOIN exam_manage tbl4 ON tbl3.exam_id = tbl4.exam_id
where tbl1.del_flag = 0
and tbl2.del_flag = 0
and tbl3.del_flag = 0
and tbl4.del_flag = 0

    AND tbl1.dept_id = #{unitId}


    AND tbl2.candidate_name = #{candidateName}


    AND tbl2.mobile = #{mobile}


    AND tbl4.exam_year = #{examYear}


${params.dataScope}
